import { ProductsService } from './../../../../../../libs/shared/src/lib/services/products.service';
import { Location } from "@angular/common";
import { Component } from '@angular/core';
import { FormGroup, UntypedFormControl, Validators } from '@angular/forms';
import { Category } from '@medcoding/shared';
import { Product } from 'libs/shared/src/lib/models/product';

@Component({
  selector: 'admin-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {


  productForm = new FormGroup({
      title: new UntypedFormControl('', [Validators.required]),
      description: new UntypedFormControl('', [Validators.required]),
      brand: new UntypedFormControl('', [Validators.required]),
      stock: new UntypedFormControl('', [Validators.required]),
      rating: new UntypedFormControl('', [Validators.required]),
      // created_at: new UntypedFormControl('', [Validators.required]),
      // updated_at: new UntypedFormControl('', [Validators.required]),
      price: new UntypedFormControl('', [Validators.required]),
      category: new UntypedFormControl('', [Validators.required]),
  })



  constructor(private productservice: ProductsService, private location: Location) {}

  products:Product[]=[]
  categories:Category[]=[]


  myProduct:Product = {
    title:"",
    description:"",
    brand:"",
    countStock:0,
    rating:0,
    price:0,
    category:"",


  }

  submit(form: FormGroup) {

    if(form.invalid) {
      return
    }

    this.saveProduct()

  }

  saveProduct() {
    this.productservice.addProduct(this.productForm.value).subscribe(res => {
      if(res.success) {
       this.location.back()
      }
    },
    err => console.error(err))
  }


}
