import { Component, OnInit } from '@angular/core';
import { FormGroup, UntypedFormControl, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { UsersService } from 'libs/shared/src/lib/services/users.service';

@Component({
  selector: 'admin-edit-user',
  templateUrl: './edit-user.component.html',
  styleUrls: ['./edit-user.component.css']
})
export class EditUserComponent implements OnInit{


  id: string = ""
  categoryForm = new FormGroup({
    name: new UntypedFormControl('', [Validators.required]),
    email: new UntypedFormControl('', [Validators.required]),
    password: new UntypedFormControl('', [Validators.required]),
    isAdmin: new UntypedFormControl('', [Validators.required]),
    address: new UntypedFormControl('', [Validators.required]),
    city: new UntypedFormControl('', [Validators.required]),
    country: new UntypedFormControl('', [Validators.required]),
    phone: new UntypedFormControl('', [Validators.required])
  })

  constructor(private userService: UsersService, private route: ActivatedRoute, private location: Location) {}

  ngOnInit(): void {
      this.route.params.subscribe((params: any) => {
          this.userService.getUsers(params.id).subscribe(res => {
            this.id = params.id
            this.categoryForm.patchValue(res.category)
          })
      })
  }

  submit(form: FormGroup) {

    if(form.invalid) {
      return
    }

    this.updateCategory()

  }

  updateCategory() {
    this.categoryService.updateCategory(this.id, this.categoryForm.value).subscribe(res => {
      if(res.success) {
       this.location.back()
      }
    },
    err => console.error(err))
  }

}

<div class="row mt-3">
  <div class="col-md-6">
      <h1>Edit category</h1>
  </div>
  <div class="col-md-6 text-end"><a routerLink="/admin/category" class="btn btn-success">Back to list</a></div>
</div>

<div class="row">
  <div class="col-md-4 mx-auto mt-3">


    <form [formGroup]="categoryForm" (submit)="submit(categoryForm)">

          <div class="form-group">
              <label for="label">Label</label>
              <input formControlName="label" id="label" type="text" class="form-control">
          </div>

          <div class="form-group">
              <label for="icon">Icon</label>
              <input formControlName="icon" id="icon" type="text" class="form-control">
          </div>

          <div class="form-group">
              <label for="color">Color</label>
              <input formControlName="color" id="color" type="color" class="form-control">
          </div>

          <div class="d-grid mt-2">
              <button class="btn btn-warning">Update category</button>
          </div>

      </form>
  </div>
</div>
