import { CategoryService, Category, ResCategory } from '@medcoding/shared';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'admin-listcategory',
  templateUrl: './list-category.component.html',
  styleUrls: ['./list-category.component.scss'],
  providers: [CategoryService]
})
export class ListCategoryComponent implements OnInit {

  categories: Category[] = []

  constructor(private categoryService: CategoryService) {}

  ngOnInit(): void {
      this.getCategories()
  }

  getCategories() {
    this.categoryService.getAllCategories().subscribe(({success, categories}: ResCategory) => {
      if(success) {

        this.categories = categories
      }
    })
  }

  deleteCategory(id:string ){
    this.categoryService.deleteCategory(id).subscribe(()=>{
      this.getCategories();
    })
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-2 mt-2">
      <div class="col-md-6 text-end"><a routerLink="/admin" class="btn btn-link"><i class="fa fa-arrow-left" aria-hidden="true"></i> Back</a></div>
    </div>
    <div class="col-md-10">
    </div>
  </div>


    <div class="row mt-5">
      <div class="col-md-6">
        <h2>List of Categories</h2>
      </div>
      <div class="col-md-6 text-end">
        <a href="" class="btn btn-link" routerLink="/admin/addCategory"><i class="fa fa-plus" aria-hidden="true"></i> New Category</a>
      </div>
    </div>

    <div class="row mt-5">
      <div class="col-md-12">
        <table class="table table-striped">
          <thead class="">
            <tr>
              <th scope="col">Label</th>
              <th scope="col">Icon</th>
              <th scope="col">Color</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let category of categories">
              <td [ngStyle]="{color: category.color}">{{category.label}}</td>
              <td [ngStyle]="{color: category.color}"><i class="fa " [ngClass]="category.icon" ></i></td>
              <td [ngStyle]="{color: category.color}">{{category.color}}</td>
              <td class="text-end">
                <a [routerLink]="['/admin/category/edit/', category._id]" class="btn btn-warning me-2"><i class="fa fa-pencil-square-o" aria-hidden="true"></i></a>
                <button (click)="deleteCategory(category?._id ?? '')" class="btn btn-danger"><i class="fa fa-trash" aria-hidden="true"></i></button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>

</div>
