import { ActivatedRoute } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { ProductsService } from 'libs/shared/src/lib/services/products.service';
import { FormGroup, UntypedFormControl, Validators } from '@angular/forms';
import { Category, CategoryService, ResCategory } from '@medcoding/shared';
import { Location } from '@angular/common'

@Component({
  selector: 'admin-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.css']
})
export class EditProductComponent implements OnInit {


  id: string = ""
  categories:Category[]=[]
  thumbnail : any;

  productForm = new FormGroup({
    title: new UntypedFormControl('', [Validators.required]),
    description: new UntypedFormControl('', [Validators.required]),
    brand: new UntypedFormControl('', [Validators.required]),
    countStock: new UntypedFormControl('', [Validators.required]),
    rating: new UntypedFormControl('', [Validators.required]),
    // created_at: new UntypedFormControl('', [Validators.required]),
    // updated_at: new UntypedFormControl('', [Validators.required]),
    price: new UntypedFormControl('', [Validators.required]),
    category: new UntypedFormControl('', [Validators.required]),
    thumbnail: new UntypedFormControl(''),
})

  constructor(private productservice: ProductsService, private route: ActivatedRoute, private location: Location, private categoryservice: CategoryService){}

  ngOnInit(): void {
    this.route.params.subscribe((params: any) => {
      this.productservice.getProduct(params.id).subscribe(res => {
        this.id = params.id
        this.productForm.patchValue(res.product)
        this.productForm.controls['category'].value
      })
  })


    this.getCategories()
  }

  submit(form: FormGroup) {

    if(form.invalid) {
      return
    }

    this.updateProduct
  }

onSelectFile(event: any) {
    if (event.target.files.length > 0) {
      const file = event.target.files[0];
      const mimeType = event.target.files[0].type;
      if (mimeType.match(/image\/*/) == null) {
          return ;
      }
      this.thumbnail = file;
}
  }

  updateProduct() { //  this.location.back()
    const formData = new FormData() ;
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const product = {
    title: this.productForm.controls['title'].value,
    discription: this.productForm.controls['description'].value,
    // content: this.productForm.controls['content'].value,
    rating: this.productForm.controls['rating'].value,
    brand:this.productForm.controls['brand'].value,
    countStock: this.productForm.controls['countStock'].value,
    price: this.productForm.controls['price'].value ,
    category : this.productForm.controls['category'].value
  }

  console.log(product, 'ediiiit')
    formData.append('thumbnail', this.thumbnail) ;
    formData.append('product' , JSON.stringify(product) ) ;

    this.productservice.updateProduct(this.id,formData).subscribe(res => {
      if(res.success) {
        this.location.back()
      }
    },
    err => console.error(err))
  }


  getCategories(){
    this.categoryservice.getAllCategories().subscribe((res: ResCategory) => this.categories = res.categories);
    }
}

<div class="container mx-auto">
  <div class="row mt-2">
    <div class="col-md-6">

        <a routerLink="/admin/products" class="btn btn-link"><i class="fa fa-arrow-left" aria-hidden="true"></i> Back</a>

    </div>
  </div>
  <div class="row">
    <div class="col-md-6 mx-auto">


      <form [formGroup]="productForm" (submit)="submit(productForm)">
        <div class="mb-3">
          <label for="title" class="form-label">Title</label>
          <input type="text" class="form-control" id="title" formControlName="title">
        </div>
        <div class="mb-3">
          <label for="description" class="form-label">Description</label>
          <textarea class="form-control" id="description" formControlName="description"></textarea>
        </div>
        <div class="mb-3">
          <label for="brand" class="form-label">Brand</label>
          <input type="text" class="form-control" id="brand" formControlName="brand">
        </div>
        <div class="mb-3">
          <label for="stock" class="form-label">Stock</label>
          <input type="number" class="form-control" id="stock" formControlName="countStock">
        </div>
        <div class="mb-3">
          <label for="rating" class="form-label">Rating</label>
          <input type="number" class="form-control" id="rating" formControlName="rating" name="rating">
        </div>
        <div class="mb-3">
          <label for="thumbnail" class="form-label">Thumbnail</label>
          <input type="file" (change)="onSelectFile($event)" class="form-control" id="thumbnail" formControlName="thumbnail">
        </div>
        <!-- <div class="mb-3">
          <label for="created_at" class="form-label">Created At</label>
          <input type="date" class="form-control" id="created_at" formControlName="created_at">
        </div>
        <div class="mb-3">
          <label for="updated_at" class="form-label">Updated At</label>
          <input type="date" class="form-control" id="updated_at" formControlName="updated_at">
        </div> -->
        <div class="mb-3">
          <label for="price" class="form-label">Price</label>
          <input type="number" class="form-control" id="price" formControlName="price">
        </div>
        <div class="mb-3">
          <select class="form-select"  aria-label="Default select example" formControlName="category" name="category" id="category">
            <option *ngFor="let cat of categories" [value]="cat?._id" >{{cat.label}}</option>
          </select>
        </div>

        <div class="text-center">
          <button type="submit"  class="btn btn-primary">Submit</button>
        </div>
      </form>

    </div>
  </div>

</div>
