import { FormGroup, UntypedFormControl, Validators } from '@angular/forms';
import { AuthService } from './../../services/auth.service';
import { Component } from '@angular/core';

@Component({
  selector: 'users-signin',
  templateUrl: './signin.component.html',
  styleUrls: ['./signin.component.css']
})
export class SigninComponent {

  constructor (private AuthService:AuthService){}

  loginForm = new FormGroup({
    email: new UntypedFormControl('',Validators.required),
    password: new UntypedFormControl('',Validators.required)
  })


  // singIN(email:string, password:string){
  //   let {email , password} = this.loginForm.value;
  //   this.AuthService.login(this.loginForm.value).subscribe
  // }

  submit(){
    console.log(this.loginForm);

    // if(this.loginForm.invalid){
    //   return this.singIN
    // }
  }

}
<div class="row my-5">
  <div class="col-md-4 my-3 mx-auto">
    <h1 class="text-center my-3">Sign In</h1>
    <form [formGroup]="loginForm" (ngSubmit)="submit  ()">
      <div class="form-group my-3">
        <label for="email">Email</label>
        <input formControlName="email" type="text" class="form-control" placeholder="your E-mail" id="email">
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input formControlName="password" type="password" name="" id="password" placeholder="your" password="" class="form-control"></div>
      <div class="my-3">
        <button class="btn btn-success">Sign In</button>
      </div>
    </form>
  </div>
</div>
