import { ProductsService } from './../../../../../../lib/products.service';

import { Component } from '@angular/core';
import { FormGroup, UntypedFormControl, Validators } from '@angular/forms';
import { Product } from 'libs/shared/src/lib/models/product';

@Component({
  selector: 'admin-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {


  categoryForm = new FormGroup({
    label: new UntypedFormControl('', [Validators.required]),
    icon: new UntypedFormControl('', [Validators.required, Validators.pattern(/[a-zA-Z-]*/)]),
    color: new UntypedFormControl(),
  })



  constructor(private productservice: ProductsService, private location: Location) {}

  products:Product[]=[]


  myProduct:Product = {
    // label:"",
    // color:"",
    // icon:"",
  }

  submit(form: FormGroup) {

    if(form.invalid) {
      return
    }

    this.saveProduct()

  }

  saveCategory() {
    this.categoryService.addCategory(this.categoryForm.value).subscribe(res => {
      if(res.success) {
       this.location.back()
      }
    },
    err => console.error(err))
  }


}
